          <div class="px-4 pt-4">
            <a href="/users/sign_in" class="block font-bold text-gray-900 hover:text-gray-700">
              登录
            </a>
          </div>
          <div class="p-4">
            <a href="/users/sign_up"
               class="block px-3 py-3 font-bold text-center text-white bg-purple-100 rounded hover:bg-purple-100 focus:outline-none focus:bg-indigo-600">
              联系我们
            </a>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>
<script>
  const OFFSET = 60

  export default {
    data() {
      return {
        isOpen: false,
        scrolled: false,
        showNavbar: true,
        lastScrollPosition: 0,
        scrollValue: 0,
        menuList: [
          {
            name: '服务',
            link: 'service'
          },
          {
            name: '团队',
            link: 'team'
          },
          {
            name: '关于',
            external: true,
            link: 'https://w.caixie.top/'
          },
          // {
          //   name: '伙伴',
          //   link: 'partner'
          // },
          // {
          //   name: 'FAQs',
          //   link: ''
          // }
        ]
      }
    },
    mounted() {
      this.lastScrollPosition = window.pageYOffset
    },
    methods: {
      open() {
        this.isOpen = true
        this.$nextTick(() => {
          this.$refs.closeButton.focus()
        })
      },
      close() {
        this.isOpen = false
        this.$nextTick(() => {
          this.$refs.openButton.focus()
        })
      },
      onScroll () {
        if (window.pageYOffset < 0) {
          return
        }
        if (Math.abs(window.pageYOffset - this.lastScrollPosition) < OFFSET) {
          return
        }
        this.showNavbar = window.pageYOffset < this.lastScrollPosition
        this.lastScrollPosition = window.pageYOffset
        this.scrollValue = window.scrollY - OFFSET
      },
      toggleMenu() {
        this.menuDisplay = !this.menuDisplay
      },
      onClickMenu(id) {
        this.menuDisplay = false
        let selector = id ? `#${id}` : 'body'
        if (process.client) {
          document.querySelector(selector).scrollIntoView({ behavior: 'smooth' })
        }
      },
    },
    computed: {
      styles() {
        return [
          !this.isOpen && 'hidden',
          !this.isOpen && 'ease-out transition-middle'
        ]
      },
      currentClass() {
        return [
          'fixedHeader',
          this.showNavbar && this.scrollValue > OFFSET ? 'fixedHeader-shadow' : ''
        ]
      },
    },
    watch: {
      isOpen: {
        immediate: true,
        handler(isOpen) {
          if (isOpen) {
            if (process.client) {
              document.body.style.setProperty('overflow', 'hidden')
            }
          } else {
            if (process.client) {
              document.body.style.removeProperty('overflow')
            }
          }
        }
      }
    }
  }
</script>
